// Validation logic for invoice data
const invoice = $input.item.json;
const errors = [];
const warnings = [];

// Required field checks
if (!invoice.invoiceNumber) errors.push('Missing invoice number');
if (!invoice.vendorName) errors.push('Missing vendor name');
if (!invoice.totalAmount || invoice.totalAmount <= 0) errors.push('Invalid total amount');
if (!invoice.invoiceDate) errors.push('Missing invoice date');
if (!invoice.dueDate) errors.push('Missing due date');

// Business logic validations
const invoiceDate = new Date(invoice.invoiceDate);
const dueDate = new Date(invoice.dueDate);
const today = new Date();

if (dueDate < invoiceDate) {
  errors.push('Due date is before invoice date');
}

if (invoiceDate > today) {
  warnings.push('Invoice date is in the future');
}

// Amount validations
if (invoice.lineItems && invoice.lineItems.length > 0) {
  const calculatedSubtotal = invoice.lineItems.reduce((sum, item) => sum + (item.amount || 0), 0);
  const expectedTotal = calculatedSubtotal + (invoice.taxAmount || 0);

  if (Math.abs(expectedTotal - invoice.totalAmount) > 0.01) {
    warnings.push(`Amount mismatch: Calculated ${expectedTotal.toFixed(2)} vs Invoice ${invoice.totalAmount.toFixed(2)}`);
  }
}

// Duplicate check (simplified - in production, check database)
const isDuplicate = false; // Would check DB here

if (isDuplicate) {
  errors.push('Duplicate invoice number detected');
}

// Determine if auto-approval is possible
const autoApprovalThreshold = 5000;
const requiresApproval = invoice.totalAmount > autoApprovalThreshold;
const daysUntilDue = Math.ceil((dueDate - today) / (1000 * 60 * 60 * 24));
const isUrgent = daysUntilDue <= 7;

return {
  json: {
    ...invoice,
    validation: {
      isValid: errors.length === 0,
      errors: errors,
      warnings: warnings,
      requiresApproval: requiresApproval,
      isUrgent: isUrgent,
      daysUntilDue: daysUntilDue
    }
  }
};

