parameters.jsCode:
// Analyze email content and calculate send score
const email = $input.item.json;
const lead = $('Get Leads Needing Contact').item.json;

// Quality checks
let qualityScore = 100;
const issues = [];

// Check personalization
if (!email.body.includes(lead.name.split(' ')[0])) {
  qualityScore -= 20;
  issues.push('Missing first name personalization');
}

if (!email.body.includes(lead.company)) {
  qualityScore -= 15;
  issues.push('Missing company name');
}

// Check length
const wordCount = email.body.split(/\s+/).length;
if (wordCount < 50 || wordCount > 200) {
  qualityScore -= 10;
  issues.push(`Word count ${wordCount} outside optimal range (50-200)`);
}

// Check for spam words
const spamWords = ['free', 'guarantee', 'amazing', 'incredible', 'limited time'];
const lowerBody = email.body.toLowerCase();
spamWords.forEach(word => {
  if (lowerBody.includes(word)) {
    qualityScore -= 5;
    issues.push(`Contains potential spam word: ${word}`);
  }
});

// Check CTA presence
if (!lowerBody.includes('?') && !lowerBody.includes('call') && !lowerBody.includes('meeting')) {
  qualityScore -= 10;
  issues.push('No clear call to action');
}

// Timing optimization
const hour = new Date().getHours();
let sendDelay = 0;

// Best sending times: 10am-12pm or 2pm-4pm
if (hour < 10) {
  sendDelay = (10 - hour) * 60; // minutes
} else if (hour > 16) {
  sendDelay = (24 - hour + 10) * 60; // next day at 10am
}

return {
  json: {
    ...email,
    leadId: lead.id,
    leadName: lead.name,
    leadEmail: lead.email,
    leadCompany: lead.company,
    qualityScore: qualityScore,
    qualityIssues: issues,
    shouldSend: qualityScore >= 70,
    sendDelay: sendDelay,
    optimalSendTime: sendDelay > 0 ? new Date(Date.now() + sendDelay * 60000).toISOString() : 'now'
  }
};

